import React from "react";
import { Link } from "react-router-dom";

const Card = ({ name, username, id }) => {
  const addFav = () => {
    const cardData = {
      name,
      username, 
      id,
    };
    const existingCards = localStorage.getItem("favCards");
    const parsedCards = existingCards ? JSON.parse(existingCards) : [];
    const updatedCards = [...parsedCards, cardData];
    localStorage.setItem("favCards", JSON.stringify(updatedCards));
  };

  const handleNameClick = (event) => {
    event.preventDefault();
  };

  const handleUsernameClick = (event) => {
    event.preventDefault();
  };

  const handleIdClick = (event) => {
    event.preventDefault();
  };


  return (
    <div className="card">
      <h2 onClick={handleNameClick}>{name}</h2>
      <p onClick={handleUsernameClick}>{username}</p>
      <p onClick={handleIdClick}>ID: {id}</p>
      <Link to={`/detail/${id}`}>View Details</Link>
      <button onClick={addFav} className="favButton">
        Add fav
      </button>
    </div>
  );
};

export default Card;
