import React, { useContext, useEffect, useReducer } from 'react';
import Card from '../Components/Card';
import { ContextGlobal } from '../Components/utils/global.context';
import Detail from './Detail';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_SUCCESS':
      return { ...state, dentists: action.payload };
    case 'ADD_TO_FAVORITES':
      return { ...state, favorites: [...state.favorites, action.payload] };
    default:
      return state;
  }
};

const initialState = {
  dentists: [],
  favorites: [],
};

const Home = () => {
  const { state: { theme }, toggleTheme, addToFavorites } = useContext(ContextGlobal);
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    const fetchDentists = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        dispatch({ type: 'FETCH_SUCCESS', payload: data });
      } catch (error) {
        console.error('Error fetching dentists:', error);
      }
    };

    fetchDentists();
  }, []);

  const handleAddToFavorites = (dentist) => {
    addToFavorites(dentist);
    dispatch({ type: 'ADD_TO_FAVORITES', payload: dentist });
  };

  return (
    <main className={theme === 'dark' ? 'dark' : 'light'}>
      <h1>Home</h1>
      <button onClick={toggleTheme}>Toggle Theme</button>
      <div className='card-grid'>
        {state.dentists.map((dentist) => (
          <div key={dentist.id}>
            <Card name={dentist.name} username={dentist.username} id={dentist.id} />
            <button onClick={() => handleAddToFavorites(dentist)}>Add to Favorites</button>
          </div>
        ))}
      </div>

      <Detail />
    </main>
  );
};

export default Home;
