import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "../Components/utils/global.context";

const Card = ({ name, username, id }) => {
  const { addToFavorites } = useContext(ContextGlobal);

  const handleAddToFavorites = () => {
    const cardData = {
      name,
      username,
      id,
    };
    addToFavorites(cardData);
  };

  const handleNameClick = (event) => {
    event.preventDefault();
  };

  const handleUsernameClick = (event) => {
    event.preventDefault();
  };

  const handleIdClick = (event) => {
    event.preventDefault();
  };

  return (
    <div className="card">
      <img src={`${process.env.PUBLIC_URL}/images/doctor.jpg`} alt="Doctor" />
      <h2 onClick={handleNameClick}>{name}</h2>
      <p onClick={handleUsernameClick}>{username}</p>
      <p onClick={handleIdClick}>ID: {id}</p>
      <Link to={`/detail/${id}`}>View Details</Link>
      <button onClick={handleAddToFavorites} className="favButton">
        Add fav
      </button>
    </div>
  );
};

export default Card;
