import React, { useState } from "react";
import { Link } from "react-router-dom";

const Card = ({ name, username, id, onAddToFavorites }) => {
  const [isAddedToFavorites, setIsAddedToFavorites] = useState(false);

  const addFav = () => {
    const cardData = {
      name,
      username, 
      id,
    };

    setIsAddedToFavorites(true);
    onAddToFavorites(cardData);
  };

  const handleNameClick = (event) => {
    event.preventDefault();
  };

  const handleUsernameClick = (event) => {
    event.preventDefault();
  };

  const handleIdClick = (event) => {
    event.preventDefault();
  };

  return (
    <div className="card">
      <img src={`${process.env.PUBLIC_URL}/images/doctor.jpg`} alt="Doctor" />
      <h2 onClick={handleNameClick}>{name}</h2>
      <p onClick={handleUsernameClick}>{username}</p>
      <p onClick={handleIdClick}>ID: {id}</p>
      <Link to={`/detail/${id}`}>View Details</Link>
      <button onClick={addFav} className={`favButton ${isAddedToFavorites ? 'added' : ''}`}>
        {isAddedToFavorites ? 'Added to Favorites' : 'Add Fav'}
      </button>
    </div>
  );
};

export default Card;
