import React, { useState } from 'react';

const Form = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [errors, setErrors] = useState({});

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform form validation
    const validationErrors = {};

    if (nombre.trim() === '') {
      validationErrors.nombre = 'Ingresa tu nombre';
    }

    if (email.trim() === '') {
      validationErrors.email = 'Ingresa tu email';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      validationErrors.email = 'Formato de email inválido';
    }

    if (mensaje.trim() === '') {
      validationErrors.mensaje = 'Ingresa tu mensaje';
    }

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Form is valid, perform further actions
    // e.g., send form data to the server

    // Reset form fields
    setNombre('');
    setEmail('');
    setMensaje('');
    setErrors({});
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="nombre">Nombre:</label>
          <input
            type="text"
            id="nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
          {errors.nombre && <p>{errors.nombre}</p>}
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {errors.email && <p>{errors.email}</p>}
        </div>
        <div>
          <label htmlFor="mensaje">Mensaje:</label>
          <textarea
            id="mensaje"
            value={mensaje}
            onChange={(e) => setMensaje(e.target.value)}
          ></textarea>
          {errors.mensaje && <p>{errors.mensaje}</p>}
        </div>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default Form;
