import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Card = ({ name, username, id, isFavorite, onAddToFavorites, onRemoveFromFavorites }) => {
  const [addedToFavorites, setAddedToFavorites] = useState(isFavorite);

  const addToFavorites = () => {
    const cardData = {
      name,
      username,
      id,
    };

    setAddedToFavorites(true);
    onAddToFavorites(cardData);
  };

  const removeFromFavorites = () => {
    setAddedToFavorites(false);
    onRemoveFromFavorites(id);
  };

  const handleCardClick = (event) => {
    if (event.target.classList.contains('favButton')) {
      return;
    }

    // Navigate to the detail page
    // You can replace `/detail/${id}` with your actual details page URL
    window.location.href = `/detail/${id}`;
  };

  return (
    <Link to={`/detail/${id}`}>
      <div className="card" onClick={handleCardClick}>
        <img src={`${process.env.PUBLIC_URL}/images/doctor.jpg`} alt="Doctor" />
        <h2>{name}</h2>
        <p>{username}</p>
        {addedToFavorites ? (
          <button onClick={removeFromFavorites} className="favButton">
            Remove from Favorites
          </button>
        ) : (
          <button onClick={addToFavorites} className="favButton">
            Add to Favorites
          </button>
        )}
      </div>
    </Link>
  );
};

export default Card;
