import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";

const Card = ({ name, username, id, isFavorite, onAddToFavorites, onRemoveFromFavorites }) => {
  const [isAddedToFavorites, setIsAddedToFavorites] = useState(isFavorite);
  const history = useHistory();

  const handleNameClick = (event) => {
    event.preventDefault();
  };

  const handleUsernameClick = (event) => {
    event.preventDefault();
  };

  const handleAddToFavorites = () => {
    setIsAddedToFavorites(true);
    onAddToFavorites({ name, username, id });
  };

  const handleRemoveFromFavorites = () => {
    setIsAddedToFavorites(false);
    onRemoveFromFavorites(id);
  };

  const handleCardClick = () => {
    history.push(`/detail/${id}`);
  };

  return (
    <div className="card" onClick={handleCardClick}>
      <img src={`${process.env.PUBLIC_URL}/images/doctor.jpg`} alt="Doctor" />
      <h2 onClick={handleNameClick}>{name}</h2>
      <p onClick={handleUsernameClick}>{username}</p>
      <div className="card-buttons">
        {isAddedToFavorites ? (
          <button onClick={handleRemoveFromFavorites} className="favButton remove">
            Remove from Favorites
          </button>
        ) : (
          <button onClick={handleAddToFavorites} className="favButton add">
            Add to Favorites
          </button>
        )}
      </div>
    </div>
  );
};

export default Card;
